import React from 'react';
import face from './faces.png';
import styles from './About.css';

class About extends React.Component {
    render() {
        const box = {
            border: '4px solid white',
            display: 'flex'
        };

        const image = {
            textAlign: "left",
            padding: "40px 20px",
            height: "350px"
        };

        const style1 = {
            padding: "40px 20px",
            color: "blue",
            background: "white"
        };

        return (
            <div>
                <p className='tc .bg-light-yellow'>About</p>
                <div style={box}>
                    <img src={face} style={image} alt='pic of faces' />
                    <p style={style1}> <mark>This is a sample paragraph to test the working of the program</mark></p>
                </div>
            </div>
        );
    }
}

export default About;
